import { Button, StyleSheet, TextInput, TouchableOpacity, TouchableHighlight, Image, Alert } from 'react-native';
import { Text, View } from '../components/Themed';
import { RootStackScreenProps } from '../../types';

{/* <Text style={styles.title}>Login</Text>
<Button title="Home" onPress={() => navigation.navigate('Home')} /> */}
const Login = ({ navigation }: RootStackScreenProps<'Login'>) => {

  const login = () => {
   console.log('LOGIN SUCCESSFUL')
  };

  return (
    <View style={styles.container}>
			<TouchableOpacity onPress={() => navigation.navigate('Home')}>
      Go Home</TouchableOpacity>
				<View style={styles.inputContainer}>
					<TextInput
						style={styles.inputs}
						placeholder="Email"
						keyboardType="email-address"
						underlineColorAndroid="transparent"
						autoCapitalize="none"
					/>
				</View>

        <View style={styles.inputContainer}>
          <TextInput
            style={styles.inputs}
            placeholder="Password"
            secureTextEntry={true}
            underlineColorAndroid="transparent"
            autoCapitalize="none"
          />
        </View>
        <TouchableHighlight
          style={[styles.buttonContainer, styles.loginButton]}
          onPress={() => login()}
        >
          <Text style={styles.loginText}>Login</Text>
        </TouchableHighlight>
				<Text>Are registered?</Text>
				<View
					style={{
						display : 'flex'
					}}
				>
					<TouchableOpacity
						style={styles.buttonContainer}
						onPress={() => navigation.navigate('Signup')}
					>
						<Text>Register</Text>
					</TouchableOpacity>
				</View>
    </View>
  );
}
export default Login;


const styles = StyleSheet.create({
	container       : {
		flex            : 1,
		justifyContent  : 'center',
		alignItems      : 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
	inputContainer  : {
		borderBottomColor : '#F5FCFF',
		backgroundColor   : '#FFFFFF',
		borderRadius      : 5,
		borderBottomWidth : 1,
		width             : 250,
		height            : 50,
		marginBottom      : 20,
		flexDirection     : 'row',
		alignItems        : 'center'
	},
	inputs          : {
		height            : 50,
		marginLeft        : 16,
		borderBottomColor : '#FFFFFF',
		flex              : 1
	},
	inputIcon       : {
		width          : 30,
		height         : 30,
		marginLeft     : 15,
		justifyContent : 'center'
	},
	buttonContainer : {
		justifyContent : 'center',
		alignItems     : 'center',
		marginBottom   : 20,
		width          : 250,
		margin         : 4,
		padding        : 5,
		borderRadius   : 5,
		borderWidth    : 1,
		borderColor    : '#85c4ea',
		maxHeight      : 100,
		alignSelf      : 'center'
	},
	loginButton     : {
		backgroundColor : '#00b5ec'
	},
	loginText       : {
		color : 'white'
	}
});
