import { Button, StyleSheet, TextInput, TouchableOpacity, TouchableHighlight, Image, Alert } from 'react-native';
import { Text, View } from '../components/Themed';
import { RootStackScreenProps } from '../../types';

const Signup = ({ navigation }: RootStackScreenProps<'Signup'>) => {

  const signup = () => {
    console.log('SIGNUP SUCCESSFUL')
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Zula</Text>
      <Text style={styles.subtitle}>The Reading Tribe</Text>
      <Text style={styles.description}>Join our reading tribe</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.inputs}
          placeholder="Name"
          keyboardType="default"
          underlineColorAndroid="transparent"
          autoCapitalize="none"
        />
      </View>

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.inputs}
          placeholder="Email"
          keyboardType="email-address"
          underlineColorAndroid="transparent"
          autoCapitalize="none"
        />
      </View>

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.inputs}
          placeholder="Password"
          secureTextEntry={true}
          underlineColorAndroid="transparent"
          autoCapitalize="none"
        />
      </View>
      <TouchableHighlight
        style={[styles.buttonContainer, styles.loginButton]}
        onPress={() => signup()}
      >
        <Text style={styles.loginText}>Signup</Text>
      </TouchableHighlight>
      <View
        style={{
          display: 'flex'
        }}
      >
        <TouchableOpacity
          style={styles.buttonContainer}
          onPress={() => navigation.navigate('Login')}
        >
          <Text>Login</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
export default Signup;


const styles = StyleSheet.create({
  container: {
    flex: 1,
   /*  justifyContent: 'center', */
    alignItems: 'center',
  },
  title: {
    fontSize: 36,
    fontWeight: 'bold',
    marginTop: 140
  },
  subtitle: {
    fontSize: 24,
    marginBottom: 50,
    lineHeight: 30
  },
  description: {
    color: '#ffffff',
    fontSize: 60,
    marginBottom: 50,
    lineHeight: 69,
    fontWeight: 'bold',
  },
  inputContainer: {
    backgroundColor: '#FFFFFF',
    borderRadius: 5,
    borderBottomWidth: 1,
    width: 620,
    height: 60,
    marginBottom: 20,
    flexDirection: 'row',
    alignItems: 'center'
  },
  inputs: {
    height: 60,
    flex: 1
  },
  buttonContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
    width: 250,
    margin: 4,
    padding: 5,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: '#A0DAB3',
    maxHeight: 100,
    alignSelf: 'center'
  },
  loginButton: {
    backgroundColor: '#A0DAB3'
  },
  loginText: {
    color: 'black'
  }
});
