import { Button, StyleSheet, TextInput, TouchableOpacity, TouchableHighlight, Image, Alert } from 'react-native';
import { Text, View } from '../components/Themed';
import { RootStackScreenProps } from '../../types';

const Login = ({ navigation }: RootStackScreenProps<'Login'>) => {

  const login = () => {
    console.log('SIGNUP SUCCESSFUL')
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Zula</Text>
      <Text style={styles.subtitle}>The Reading Tribe</Text>
      <Text style={styles.description}>Welcome!</Text>
      <Text style={[styles.description, styles.subText]}>Its Good To See You.</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.inputs}
          placeholder="Name"
          keyboardType="default"
          underlineColorAndroid="transparent"
          autoCapitalize="none"
        />
      </View>

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.inputs}
          placeholder="Email"
          keyboardType="email-address"
          underlineColorAndroid="transparent"
          autoCapitalize="none"
        />
      </View>

      <View style={styles.inputContainer}>
        <TextInput
          style={styles.inputs}
          placeholder="Password"
          secureTextEntry={true}
          underlineColorAndroid="transparent"
          autoCapitalize="none"
        />
      </View>

      <View style={styles.buttonContainer} >
      <TouchableOpacity
          style={[styles.button, styles.resetPassword]}
          onPress={() => navigation.navigate('Signup')}
        >
          <Text>Did you forget your Password</Text>
        </TouchableOpacity>

        <TouchableHighlight
          style={[styles.button, styles.loginButton]}
          onPress={() => login()}
        >
          <Text style={styles.loginText}>Login</Text>
        </TouchableHighlight>
      </View>
    </View>
  );
}
export default Login;


const styles = StyleSheet.create({
  container: {
    flex: 1,
    /*  justifyContent: 'center', */
    alignItems: 'center',
  },
  title: {
    fontSize: 36,
    fontWeight: 'bold',
    marginTop: 140
  },
  subtitle: {
    fontSize: 24,
    marginBottom: 50,
    lineHeight: 30
  },
  description: {
    color: '#ffffff',
    fontSize: 60,
    textAlign: 'center',
    width: 620,
    lineHeight: 69,
    fontWeight: 'bold',
  },
  subText: {
    marginBottom: 50,
  },
  inputContainer: {
    backgroundColor: '#FFFFFF',
    borderRadius: 5,
    borderBottomWidth: 1,
    width: 620,
    height: 60,
    flexDirection: 'row',
    alignItems: 'center'
  },
  inputs: {
    height: 60,
    flex: 1
  },
  buttonContainer: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  button: {
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center'
  },
  resetPassword: {
    width: 239,
  },
  loginButton: {
    height: 56,
    width: 265,
    backgroundColor: '#A0DAB3',
    margin: 4,
    padding: 20,
    borderRadius: 100,
    marginBottom: 20,
    borderWidth: 1,
    borderColor: '#A0DAB3',
  },
  loginText: {
    color: 'black'
  }
});
